var items = companyValuesByCode("SI08004000");
var total_sum = 0.0;
var total_count = 0;

for (entry : items) {
    var values = entry.second;
    total_sum = total_sum + sum(values);
    total_count = total_count + size(values);
}

if (total_count == 0) { return 0.0; }
return round(total_sum / total_count);


