        var items = companyValuesByCode("SI12002000");

var acc = 0.0;
var c = 0;

for (item : items) {
    acc = acc + sum(item.second);
    c = c + 1;
}

if (c == 0) {
    return 0.0;
}

return acc * 1.0 / c * 1.0;


