item.fsiRule:

var codes = [
    "SI02029001": 0,
    "SI02029002": 0
];

for (code : codes) {
    var item = companyValuesByCodeAndCompanyName(code.first, companyName);
    codes[code.first] = item;
}

var result = 0.0;

if (codes["SI02029002"] != 0) {
    result = codes["SI02029001"] * 1.0 / codes["SI02029002"] * 1.0;
} else {
    result = 0;
}

return result;

